<style>
.flottform {
	--max-bubble-size: 50px;
	--max-bubble-delta: 20px;
	animation: bubbles 2s linear infinite;
	background:
		radial-gradient(circle, white var(--max-bubble-size), #0000 0) calc(var(--max-bubble-size) * 1)
			calc(100% + var(--max-bubble-size)) / calc(var(--max-bubble-size) * 3)
			calc(var(--max-bubble-size) * 3) repeat-x,
		radial-gradient(circle, white var(--max-bubble-size), #0000 0) calc(var(--max-bubble-size) * 2)
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2) /
			calc(var(--max-bubble-size) * 3) calc(var(--max-bubble-size) * 3) repeat-x,
		radial-gradient(circle, white var(--max-bubble-size), #0000 0) calc(var(--max-bubble-size) * 3)
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta)) /
			calc(var(--max-bubble-size) * 3) calc(var(--max-bubble-size) * 3) repeat-x,
		linear-gradient(to bottom, #15047c, #00e2c1);
	background-position-y: calc(100% + var(--max-bubble-size)),
		calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2),
		calc(100% + var(--max-bubble-size) + var(--max-bubble-delta)), 0;
	height: 100%;
	width: 100%;
}

@keyframes bubbles {
	0%,
	100% {
		background-position-y: calc(100% + var(--max-bubble-size)),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta)), 0;
	}

	25% {
		background-position-y: calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2),
			calc(100% + var(--max-bubble-size)),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2), 0;
	}
	50% {
		background-position-y: calc(100% + var(--max-bubble-size) + var(--max-bubble-delta)),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2),
			calc(100% + var(--max-bubble-size)), 0;
	}
	75% {
		background-position-y: calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta)),
			calc(100% + var(--max-bubble-size) + var(--max-bubble-delta) / 2), 0;
	}
}
</style>
